import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient'; // Adjust the import path as necessary
import '../styles/DetailPage.css';

const TaskDetails = ({ taskId }) => {
  const [task, setTask] = useState(null);

  useEffect(() => {
    const fetchTaskDetails = async () => {
      const { data, error } = await supabase
        .from('tasks') // Replace with your table name
        .select('*')
        .eq('id', taskId)
        .single();

      if (error) {
        console.error('Error fetching task details:', error);
      } else {
        setTask(data);
      }
    };

    if (taskId) {
      fetchTaskDetails();
    }
  }, [taskId]);

  if (!task) {
    return <div>Loading...</div>;
  }

  return (
    <div className="task-details">
      <h1>TASK DETAILS</h1>
      <div className="task-info">
        <p><strong>TASK TITLE:</strong> {task.task_name}</p>
        <p><strong>ASSIGNEE:</strong> <img src={task.assignee_image_url} alt="assignee" className="assignee-image" /> {task.assigned_to}</p>
        <p><strong>TIME LINE:</strong> {task.start_date} - {task.due_date}</p>
        <p><strong>TASK STATUS:</strong> {task.status}</p>
        <p><strong>TASK PRIORITY:</strong> {task.priority}</p>
        <p><strong>TASK DESCRIPTION:</strong></p>
        <p>{task.description}</p>
      </div>
    </div>
  );
};

export default TaskDetails;
